$('.income-height-block').each(function (index) {
    $(this).on('click', () => {
        if ($('.income-container-none-height').eq(index).hasClass('active')) {
            $('.income-height-hover').removeClass('open');
            $('.svg-rotate').removeClass('rotate');
            $('.income-container-none-height').removeClass('active')
        } else {
            $('.income-height-hover').removeClass('open');
            $('.svg-rotate').removeClass('rotate');
            $('.income-container-none-height').removeClass('active')

            $('.income-height-hover').eq(index).addClass('open');
            $('.svg-rotate').eq(index).addClass('rotate');
            $('.income-container-none-height').eq(index).addClass('active')
        }
    })
})

