document.addEventListener('DOMContentLoaded', () => {

    const swiper = new Swiper(".reviews-slider", {
        breakpoints: {
            320: {
            slidesPerView: 1,
            spaceBetween: 15
            },
            500: {
            slidesPerView: 2,
            spaceBetween: 10
            },
            999: {
            slidesPerView: 3,
            spaceBetween: 15
            }
        },

        navigation: {
            nextEl: ".btn-next",
            prevEl: ".btn-prev",
        },

    });
});

