$('.header-burger-btn').on('click', function()  {
    $('body').addClass('body-scroll');
    $('.header-burger').addClass('burger-open');
    $('.header').addClass('header-close');
})

$('.burger-menu-close').each(function() {
    $(this).on('click', () => {
        $('body').removeClass('body-scroll');
        $('.header-burger').removeClass('burger-open');
        $('.header').removeClass('header-close');
    })
})

$('#header-burger-close').on('click', function() {
    $('body').removeClass('body-scroll');
    $('.header-burger').removeClass('burger-open');
    $('.header').removeClass('header-close');
})